package com.mathpar.LABS.lab04;

import mpi.MPI;
import mpi.MPIException;

import java.util.Arrays;

public class Task4 {
    public static void main(String[] args)
            throws Exception {
        MPI.Init(args);
        // rank
        int myrank = MPI.COMM_WORLD.getRank();

        // size
        int np = MPI.COMM_WORLD.getSize();
        int n = (int) Math.pow(2, myrank);

        int arraySize = ((int) Math.pow(2, np) - 1);
        int[] q = new int[arraySize];
        int[] a = new int[n];

        // recvcount
        int[] recvcount = new int[np];

        for (int i = 0; i < np; i++) {
            recvcount[i] = (int) Math.pow(2, i);
        }

        // displs
        int[] displs = new int[np];
        for (int i = 0, m = 0; i < np; i++) {
            displs[i] = m;
            m += recvcount[i];
        }
        if (myrank == 2) {
            Arrays.fill(q, myrank);
                System.out.println("rank Buff = " + myrank + " : MAIN array = "
                        + Arrays.toString(q));

        }

        MPI.COMM_WORLD.barrier();
        MPI.COMM_WORLD.scatterv(q, recvcount, displs, MPI.INT, a, n, MPI.INT,
                2);
        System.out.println("rank = " + myrank + " : array = "
                + Arrays.toString(a));

        MPI.Finalize();
    }
}

